import { ProductService } from 'product.service';
import { Product } from './../../../../products/models/product';
import { ActivatedRouteSnapshot, Resolve, RouterStateSnapshot } from '@angular/router';
import { BehaviorSubject, Observable } from 'rxjs';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class DetailsService implements Resolve<Product>{

  constructor(private productService:ProductService) { }
  resolve(route: ActivatedRouteSnapshot, state: RouterStateSnapshot): Observable<Product>{
    return  this.productService.getById(route.params['id'])
  }

  private idSource:BehaviorSubject<string>=new BehaviorSubject<string>('0')
  productId=this.idSource.asObservable()

  get product_Id(){
    return this.productId
  }


}
