  {
    component:ShellComponent,
    path:'',
    children:[
      {
        path:'shop-details',
        component:ShopDetailsComponent
      },
      {
        path:'home',
        component:HomeComponent

      }
    ]
  },
  {
    component:ShopingCartComponent,
    path:'cart'
  },
  {
    component:ShopComponent,
    path:'shop/:category'
  },


];

